UPDATE `locales_quest` SET `Title_loc7`='Pusillín y el ancestro Azj\'Tordin' WHERE `entry`=7441;
UPDATE `locales_quest` SET `Details_loc7`='Bajé la guardia un solo momento... Intentaba escapar de las garras del príncipe caído y, en la confusión... me robaron. Un ser abominable, el diablillo Pusillín, me robó el libro de Conjuros y la llave de las antaño magníficas salas de Eldre\'Thalas.$B$BLa llave ya no me preocupa, pues ya cambié mi inmortalidad por la libertad, pero necesito desesperadamente mi libro de Conjuros.$B$BEncuentra a ese diablillo Pusillín y recupera mi libro.$B$BEmpieza a buscarlo en el Barrio Alabeo de La Masacre.' WHERE `entry`=7441;
