UPDATE `locales_quest` SET `Title_loc6`='El servidor público' WHERE `entry`=433;
UPDATE `locales_quest` SET `Details_loc6`='Como miembro del Senado y de la Liga de Exploradores, he asumido la responsabilidad de ocuparme de la infestación trogg que ha invadido nuestras tierras.$B$BPorque, sin motivo alguno, han convertido en un caos la Cantera de Gol\'Bolar. Van saliendo a medida que profundizamos en nuestra excavación, y destruyen nuestro equipo y ahuyentan a nuestros mineros. Así que tenemos que ir exterminando a cuantos podemos, luego reconstruimos todo y volvemos al trabajo.$B$BSi me ayudas con los troggs, te recompensaré.' WHERE `entry`=433;
