UPDATE `quest_greeting` SET `content_loc6`='Querido $g chico:chica;, has llegado justo a tiempo para ayudar a la Fuerza Expedicionaria de Kargath.' WHERE `entry`=9078 && `type`=0;
UPDATE `quest_greeting` SET `content_loc6`='Nunca me pierdo...' WHERE `entry`=9081 && `type`=0;
UPDATE `quest_greeting` SET `content_loc6`='¡Atención, soldado! ¡B$BWINKY! ¡FUERA SONIDO!' WHERE `entry`=9177 && `type`=0;
UPDATE `quest_greeting` SET `content_loc6`='La búsqueda de la riqueza es la única meta para un duende respetable.$B$BBBBueno, tal vez la riqueza... ¡y una muerte grande y ruidosa!' WHERE `entry`=9536 && `type`=0;
UPDATE `quest_greeting` SET `content_loc6`='Saludos, $R.' WHERE `entry`=9562 && `type`=0;
