UPDATE `locales_quest` SET `RequestItemsText_loc6`='Sí, ¿qué vas a necesitar, mon?  Tengo muchas cosas de las que ocuparme hoy, y tú no estás en mi agenda como una de ellas.  Moléstame y me aseguraré de que eso cambie.' WHERE `entry`=3541;
