UPDATE `locales_quest` SET `RequestItemsText_loc6`='Tranquilo, soldado.  Si estás aquí para descargar tierra de Un\'Goro, ¡excelente entonces!  De lo contrario, desaparece.' WHERE `entry`=3761;
UPDATE `locales_quest` SET `RequestItemsText_loc6`='Ah, sí, la suciedad.  El Archidruida quiere veinte cargas por aventurero, y veinte cargas obtendrá.  ¡Que caigan en el camino los que conspiran contra nosotros!' WHERE `entry`=3764;
UPDATE `locales_quest` SET `RequestItemsText_loc6`='La paz y la serenidad sean tuyas, amigo.  Estás aquí por negocios del mismísimo Archidruida, ¿sí?' WHERE `entry`=3781;
