UPDATE `locales_quest` SET `OfferRewardText_loc6`='Este barril está medio enterrado entre el barro y la hierba...' WHERE `entry`=285;
UPDATE `locales_quest` SET `OfferRewardText_loc6`='El capitán del Blind Princess se sentirá aliviado al saber que este precioso cargamento ha sido recuperado.  Bien se ha ganado este pago por los servicios prestados.$B$B Es bueno ver gente confiable como tú, especialmente en tiempos difíciles como estos.' WHERE `entry`=286;
UPDATE `locales_quest` SET `OfferRewardText_loc6`='¡Ay!  Eso da en el clavo y afloja la lengua.  ¡Gracias amigo!' WHERE `entry`=288;
