UPDATE `locales_quest` SET `OfferRewardText_loc6`='¡Oh mi!  Este libro no ha descansado en estos estantes desde antes de la reconstrucción de Stormwind, hace años.  Habrá que restaurarlo, por supuesto, está en mal estado, pero sería un honor colocarlo dentro de nuestra colección de libros raros.$B$B Gracias, $gsir:señora;.  ¡Acepte esto como pago por devolver el libro!' WHERE `entry`=337;
