UPDATE `locales_quest` SET `OfferRewardText_loc6`='Parece que la desaparición de Velinde es más preocupante de lo que parecía originalmente.  Aun así, no puedo creer que haya tenido un mal final.  Tal vez sería beneficioso para nosotros hacer contacto con ella.$B$B $n, dado que ha mostrado un interés personal en este asunto, me gustaría contar con su ayuda.' WHERE `entry`=1038;
