UPDATE `locales_quest` SET `OfferRewardText_loc6`='Ahhh.... Gaxim te envió, ¿eh?  ¡¿Cómo sé que no me estás mintiendo?!  ¿Mmm?  Mmm...?' WHERE `entry`=1072;
