UPDATE `locales_quest` SET `OfferRewardText_loc6`='Gracias, $N.  Has prestado un servicio a los Centinelas.$B$B Me alegra ver que Kaela... e incluso Gaxim están bien.$B$B Te has ganado nuestro aprecio.' WHERE `entry`=1082;
