UPDATE `locales_quest` SET `OfferRewardText_loc6`='Gracias, $N.  Parecía un poco incierto allí por un segundo y probablemente hemos llamado demasiado la atención sobre nosotros mismos.$B$B Sin embargo, lo importante es que tenemos la muestra del mineral y deberíamos llevársela a Ziz lo antes posible.' WHERE `entry`=1090;
