UPDATE `locales_quest` SET `OfferRewardText_loc6`='Gracias, $N.$B$B La Llama Dorada ahora será devuelta a mi tierra natal, donde puede ser protegida, para nunca más ser utilizada para el mal.$B$B ¡Sin ti, todavía no tendría idea de quién soy!  Ya que hiciste tanto por mí, me gustaría que tuvieras algo especial...$B$B ¡Toma el que quieras!' WHERE `entry`=3962;
