UPDATE `locales_quest` SET `OfferRewardText_loc6`='Esta es una oportunidad para que la Horda cambie el clima político de nuestro mundo devastado por la guerra para siempre.  Escucha atentamente, $N.$B$B El fracaso de esta misión no es una opción.' WHERE `entry`=4001;
