UPDATE `locales_quest` SET `OfferRewardText_loc6`='¡Lo admito, dudo un poco tocar esas cosas, $N!  Pero si Gregan dice que funcionará, ¡entonces estoy dispuesto a intentarlo!' WHERE `entry`=4143;
UPDATE `locales_quest` SET `OfferRewardText_loc6`='¡Aquí va nada, $N!  ¡Esto llamará la atención de Larion!' WHERE `entry`=4144;
