UPDATE `locales_quest` SET `OfferRewardText_loc6`='¡Los encontraste!  ¡Bien hecho, $N!  Estas reliquias tienen un poder que no comprendo del todo, un poder que algún día aprovecharás.$B$B También traen a mi mente oscuros recuerdos, recuerdos de muerte y traición, y de la última noche de Darrowshire...' WHERE `entry`=5181;
