UPDATE `locales_quest` SET `OfferRewardText_loc6`='Ah, la última lista de los mejores alumnos de Brock.  Tengo un lote de selecciones honorarios listas para usar.  Solo necesito cincelar los nombres de los estudiantes...' WHERE `entry`=6391;
