UPDATE `locales_quest` SET `OfferRewardText_loc6`='¡Trajiste los artilugios!  ¡Maravilloso!  ¡Incluso ahora puedo escuchar su coro zumbando y zumbando en mi mente!  Oh, cuando este puesto esté listo será la maravilla del mundo, ¡obsérveme!$B$B Aquí están tus boletos, $N.  ¡Y disfrute de su estancia en la Feria!' WHERE `entry`=7895;
