UPDATE `locales_quest` SET `OfferRewardText_loc6`='Así que has armado una baraja de Portals.  Tu gesto de devolvérnoslo creará un lazo entre tú y la Luna Negra que no se olvidará pronto.  Permíteme presentarte una de las mejores cartas de la Luna Negra como pequeña muestra de nuestro agradecimiento.' WHERE `entry`=7927;
