UPDATE `locales_quest` SET `OfferRewardText_loc6`='Aquí tienes, $N.  Aquí tienes entradas para la Feria de la Luna Negra para que las disfrutes.  ¡No, como dicen, gastarlos todos en un solo lugar!' WHERE `entry`=7942;
