UPDATE `locales_quest` SET `OfferRewardText_loc6`='¡Este trato funciona para mí si funciona para ti!' WHERE `entry`=8194;
UPDATE `locales_quest` SET `OfferRewardText_loc6`='Excelente: este conjunto de monedas será una excelente adición a nuestra creciente colección... una que usaremos en la defensa de la tribu contra el surgimiento del Dios de la Sangre.  ¡Te has ganado esta ficha de honor, $N, así como el agradecimiento de la tribu!' WHERE `entry`=8195;
UPDATE `locales_quest` SET `OfferRewardText_loc6`='Un trato justo, amigo mío. Disfruta de los deliciosos frutos de los Mares del Sur, con la bendición de la tribu.' WHERE `entry`=8196;
