UPDATE `locales_quest` SET `OfferRewardText_loc6`='No puedo hacer cara o cruz de estos glifos.  ¡Nunca había visto algo así!  Espero que la investigación de Frankal pueda arrojar algo de luz sobre este lío.' WHERE `entry`=8309;
