UPDATE `locales_quest` SET `OfferRewardText_loc6`='Después de haber luchado con éxito para entrar en la posada, ahora colocas los huevos dentro del barril.  Puedes oler el mal olor que impregna el aire...' WHERE `entry`=8322;
