UPDATE `locales_quest` SET `OfferRewardText_loc6` = '$n, me has salvado de un gran deshonor. Si estuviera en mis manos, te ofrecería un lugar en mi tribu. Pero, por ahora, solo puedo ofrecerte esto por el coraje y la nobleza que has mostrado.' WHERE `entry`=630;
UPDATE `locales_quest` SET `RequestItemsText_loc6` = 'Agradezco a mis ancestros que alguien haya venido a ayudarme. ¿Tienes la llave para liberarme?' WHERE `entry`=630;
