UPDATE `locales_quest` SET `OfferRewardText_loc6` = '¡Lo que queda del puente está a salvo! Has prestado un gran servicio al rey Magni y a las gentes de Khaz Modan. $B$BSi tan solo pudiéramos resistir hasta la llegada de los refuerzos...' WHERE `entry`=633;
