UPDATE `locales_quest` SET `OfferRewardText_loc6` = 'Ah, ¿me necesitas para reconstruir este sigilo? No debería ser muy difícil... todas las piezas parecen estar aquí y será fácil reconstituir la estructura mágica.$B$BPuede que te convenga cerrar los ojos.' WHERE `entry`=640;
UPDATE `locales_quest` SET `RequestItemsText_loc6` = 'Throm-ka, $c. Zengu me ha informado de que requerías mi ayuda.' WHERE `entry`=640;
