UPDATE `locales_quest` SET `OfferRewardText_loc6` = 'Ya estamos a medio camino de conseguir extraer a Trol\'kalar de la tumba de Aterratrols. Aún no estamos seguros de dónde se encuentra el tercer sigilo porque sus poseedores parecen haberse dirigido a algún lugar cerca de Arathi pero tengo fe en que lograremos encontrarlo.' WHERE `entry`=641;
UPDATE `locales_quest` SET `RequestItemsText_loc6` = '¡$n! ¿Tienes el segundo sigilo?' WHERE `entry`=641;
UPDATE `locales_quest` SET `OfferRewardText_loc6` = '¡Ah! Has activado estos fragmentos. ¡Ya empiezo a sentirme como antes!$B$BAunque ¡qué lejos estoy aún de la liberación!' WHERE `entry`=642;
UPDATE `locales_quest` SET `RequestItemsText_loc6` = 'Este bloque de fragmentos se parece mucho a los fragmentos de Myzrael. Pero no late en ellos el mismo sentimiento de seguridad.' WHERE `entry`=642;
UPDATE `locales_quest` SET `Title_loc1` = '아라소르의 인장' WHERE `entry`=643;
UPDATE `locales_quest` SET `Objectives_loc1` = '아라소르의 인장을 찾은 후 해머폴에 있는 젠구에게 돌아가야 합니다.' WHERE `entry`=643;
UPDATE `locales_quest` SET `OfferRewardText_loc6` = 'La pérdida del jefe de la caballería será un golpe duro para los defensores de Stromgarde. Y lo que es aún mejor, significa que solo un sigilo nos separa de hacernos con Trol\'kalar.' WHERE `entry`=643;
UPDATE `locales_quest` SET `RequestItemsText_loc6` = 'No será fácil seguirle el rastro a Valorcall, $n. A caballo puede recorrer grandes distancias rápidamente. No obstante, debemos conseguir su sigilo.' WHERE `entry`=643;
