UPDATE `locales_quest` SET `OfferRewardText_loc6` = 'Vaya día, $c. La sangre de Aterratrols ya no obsesionará a mi pueblo, los sigilos necesarios para conseguir a Trol\'kalar están en nuestras manos. Pronto, muy pronto, volveremos a atacar a los odiados trols de Zul\'Gurub.' WHERE `entry`=644;
UPDATE `locales_quest` SET `RequestItemsText_loc6` = 'Has vuelto, $n. ¿Se ha roto la Iínea de Ignaeus Aterratrols?' WHERE `entry`=644;
UPDATE `locales_quest` SET `Title_loc1` = '트롤칼라' WHERE `entry`=645;
UPDATE `locales_quest` SET `Objectives_loc1` = '스트롬가드에 있는 트롤베인의 무덤에서 트롤칼라를 가지고 와야 합니다.' WHERE `entry`=645;
UPDATE `locales_quest` SET `OfferRewardText_loc6` = 'Al hacer coincidir los rasgos del rostro de la tumba de Aterratrols con los rasgos brillantes del sigilo de Ignaeus, el sigilo se consume en medio de un destello y la legendaria espada Trol\'kalar queda liberada de la pétrea mano que la empuña.' WHERE `entry`=645;
UPDATE `locales_quest` SET `RequestItemsText_loc6` = 'Los grabados de la piedra de la cara de la tumba de Aterratrols coinciden los rasgos brillantes del sigilo de Ignaeus...' WHERE `entry`=645;
UPDATE `locales_quest` SET `Title_loc1` = '트롤칼라' WHERE `entry`=646;
