UPDATE `locales_quest` SET `RequestItemsText_loc6` = 'Hola. ¿Vienes a presentar un informe?' WHERE `entry`=685;
UPDATE `locales_quest` SET `OfferRewardText_loc6` = '¿Que vienes de parte del rey Magni Barbabronce?$B$B¡Vaya, esto debe de ser importante!' WHERE `entry`=686;
UPDATE `locales_quest` SET `OfferRewardText_loc6` = 'Hola. Hola, hola, hola, hola, hola, ¡¡HOLA!!$B$BLo siento, ¡espera, no lo siento! ¡No seas grosero!$B$BOh no... no estoy diciendo más que locuras.$B$BPero... a veces la locura es mi único consuelo.' WHERE `entry`=687;
