UPDATE `locales_quest` SET `RequestItemsText_loc6` = 'Hola de nuevo. ¿Has estado cazando? ¿Tienes mis pieles?' WHERE `entry`=768;
UPDATE `locales_quest` SET `OfferRewardText_loc6` = 'Ah, bien. Gracias, $n. Mi corazón se hinche de orgullo al ver que los jóvenes se interesan por el arte de la peletería.' WHERE `entry`=769;
UPDATE `locales_quest` SET `RequestItemsText_loc6` = '¿Tienes mi material, $n?' WHERE `entry`=769;
UPDATE `locales_quest` SET `OfferRewardText_loc6` = '¡No puedo creerlo! ¿Has derrotado a Aullido Fantasma? Acabas de ganarte mi respeto con tu habilidad para la caza, $c.$B$BYo ya no cazo, pero me honraría regalarte una de mis armas.$B$BQue te traiga reconocimiento y fortuna.$B$BEsperemos que Aullido Fantasma haya encontrado al fin la paz.' WHERE `entry`=770;
UPDATE `locales_quest` SET `OfferRewardText_loc6` = 'Veo que has aprendido a recolectar en nuestra tierra sagrada, $n. En un momento te daré el Agua de los Videntes.' WHERE `entry`=771;
UPDATE `locales_quest` SET `RequestItemsText_loc6` = 'Cuando me traigas las piedras de pozo y las bellotas ámbar, te prepararé el Agua de los Videntes.' WHERE `entry`=771;
UPDATE `locales_quest` SET `OfferRewardText_loc6` = 'No tengas miedo, $n. Has superado el Rito de la Visión.' WHERE `entry`=772;
UPDATE `locales_quest` SET `OfferRewardText_loc6` = 'Has puesto gran voluntad en superar los Ritos de la Madre Tierra, $n.$B$BLos espíritus ancestrales representamos a los $r que dieron la vida para fundar y proteger Cima del Trueno. Ahora ese deber de protección pasa a ti.$B$BHas superado el Rito de la Sabiduría, $c. Entra en Cima del Trueno con orgullo.' WHERE `entry`=773;
UPDATE `locales_quest` SET `OfferRewardText_loc6` = 'Saludos, $c. Veo que has hallado el sendero hasta mi puerta.' WHERE `entry`=775;
UPDATE `locales_quest` SET `Details_loc1` = '그대는 대지모신의 의식을 충실히 수행하여 썬더 블러프에서 당당한 지위를 얻었네.$B$B그러나 애써 얻은 지위를 유지하기 위해선 주민들에게 그대의 능력을 계속 증명해 보여야만 하지.$B$B그대도 알다시피 우리는 사냥꾼들이네. 그러니 우리의 사냥 능력을 계속 보여줘야 하네. 멀고어 아래에는 알라치아라는 거대한 코도가 살고 있지. 그대의 능력을 증명해 보이려면 알라치아를 추적해 처치한 다음 알라치아의 뿔을 내게 가지고 오게.' WHERE `entry`=776;
UPDATE `locales_quest` SET `Objectives_loc1` = '썬더 블러프에 있는 케른 블러드후프가 알라치아의 뿔을 가져다 달라고 부탁했습니다.' WHERE `entry`=776;
UPDATE `locales_quest` SET `OfferRewardText_loc6` = '¡Buen trabajo!$B$BEnviaré a un grupo para retirar la piel y la carne.$B$BTu gente te debe mucho, $n.' WHERE `entry`=776;
UPDATE `locales_quest` SET `RequestItemsText_loc6` = '¿Ya has localizado a Arra’chea?' WHERE `entry`=776;
