UPDATE `locales_quest` SET `OfferRewardText_loc6` = 'Vorrel tenía muchos problemas, pero era un buen compañero...$B$B¡Malditos humanos!$B$BPero al menos te vengaste en su nombre y te lo agradezco.$B$BPor favor, déjame sola...' WHERE `entry`=1051;
