UPDATE `locales_quest` SET `OfferRewardText_loc6` = '¿Un envío a Trinquete? Permite que compruebe los registros... $B$BNo, el envío más reciente que me consta en las salidas hacia Trinquete son unas botas de Drizzlik. No ha habido mucho más, aparte de suministros y herramientas de construcción. Nada de piezas para carreras.' WHERE `entry`=1180;
