UPDATE `locales_quest` SET `OfferRewardText_loc6` = 'Es un buen escudo. Muy fuerte, pesado, bueno. Pero Do\'gol no necesita. Yo hago escudos, no los compro, $n.$B$BDeberías saberlo.' WHERE `entry`=1321;
