UPDATE `locales_quest` SET `OfferRewardText_loc6` = 'No me gustan esas arañas que son tan grandes que no puedo sentarme en ellas para aplastarlas.$B$BGracias por conseguírmelas, $n. Ahora las abriré y verteré su contenido en el escudo...' WHERE `entry`=1322;
