UPDATE `locales_quest` SET `OfferRewardText_loc6` = 'Ah, sí, las muestras de corazón de las que habló Renferrel. Me muero por experimentar con esto... encontrar nuevas formas de toxinas siempre resulta de gran interés para la Sociedad Real de Boticarios.' WHERE `entry`=1359;
