UPDATE `locales_quest` SET `OfferRewardText_loc6` = 'Mm, a ver, déjame probar...$B$B¡No puedo creerlo! ¡Esto es... la bomba! ¡Genial!$B$BSupongo que Stoley puede quedarse el dinero que me debe. Tenía razón en cuanto al ron de la sentina Bonvapor... ¡Cómo rasca!' WHERE `entry`=2874;
