UPDATE `locales_quest` SET `OfferRewardText_loc6` = '¡Excelente! ¡Gracias, $n, voy a ponerme a trabajar de inmediato!$B$BPobre tecnobot, vaya susto te has llevado.$B$BEnseguida te arreglo; a ver, dónde estará mi llave inglesa giratoria con gato hidráulico...' WHERE `entry`=2922;
