UPDATE `locales_quest` SET `OfferRewardText_loc6` = '¡Excelente! Vamos a probar la nueva fórmula. ¡Si funciona, no solo curaremos a los gnomos leprosos, sino que me volverá a crecer el pelo! ¡Aparta!' WHERE `entry`=2926;
UPDATE `locales_quest` SET `RequestItemsText_loc6` = '¡¿No ves que tenemos muchos gnomos que curar?! ¡Necesitamos más restos radiactivos!' WHERE `entry`=2926;
UPDATE `locales_quest` SET `OfferRewardText_loc6` = 'Creí que no soportaría la pérdida del pelo, pero ya ves.$B$BEstábamos juntos cuando le cayó la nube radiactiva y se le empezó a caer el pelo a puñados. Fue horroroso. ¡Empezó a tocarse la cabeza, se le pusieron los ojos rojos y juró venganza! Desde entonces la tomó con los troggs.$B$B<Ozzie se estremece.>' WHERE `entry`=2927;
