UPDATE `locales_quest` SET `OfferRewardText_loc6` = 'Así que te envía Belgrom. Pues llegas justo a tiempo, $r.$B$BDeja que te explique la situación y luego decides si estás a la altura del desafío... Claro que si te envía Belgrom, es evidente que valor no te falta.' WHERE `entry`=2981;
