UPDATE `locales_quest` SET `OfferRewardText_loc6` = 'Ah, sí. Esta pluma pertenece a un noble grifo destinado a grandes cosas... si llega a la edad adulta.$B$BEscúchame si quieres encontrarlo, $c...' WHERE `entry`=2990;
