UPDATE `locales_quest` SET `OfferRewardText_loc6` = '¡Ah, es un temple excelente! ¡Y hay mucho! ¡Podré trabajar muchos días antes de que se acabe!$B$BGracias, $n. Por favor, acepta esto como pago.' WHERE `entry`=3042;
