UPDATE `locales_quest` SET `OfferRewardText_loc6` = 'Veo que estaba más que contento por el aspecto de su nota... un pelín demasiado contento. $B$BQuizás algún día me tome el tiempo para asegurarme de que sepa cuánto aprecio también sus insinuaciones. Bueno, al menos hay una razón para desear pasar más tiempo en Azshara. $B$BAquí tienes tu parte del pago.' WHERE `entry`=3563;
