UPDATE `locales_quest` SET `RequestItemsText_loc6` = 'Sabemos que cada una de las calderas utiliza una mezcla distinta de componentes para generar su toxina inductora de la Peste. De igual modo sabemos que tendremos que desarrollar un contraagente único para cada caldera. Tu muestra del Llanto del Dalson será la clave para comprender los componentes distintivos para esa caldera.' WHERE `entry`=5220;
