UPDATE `quest_greeting` SET `content_loc3`='Grüße, $R.' WHERE `entry`=9562 && `type`=0;
UPDATE `quest_greeting` SET `content_loc3`='Willkommen bei Kibler\'s Exotic Pets! Wie können Sie mir heute helfen?' WHERE `entry`=10260 && `type`=0;
UPDATE `quest_greeting` SET `content_loc3`='<Emberstrife nimmt deine Anwesenheit zur Kenntnis.>' WHERE `entry`=10321 && `type`=0;
UPDATE `quest_greeting` SET `content_loc3`='Whitereach Post war ursprünglich ein kleines Jagdcamp... Jetzt will sich jeder hier niederlassen und es sein Zuhause nennen.$B$B<Motega zuckt mit den Schultern>' WHERE `entry`=10428 && `type`=0;
UPDATE `quest_greeting` SET `content_loc3`='Wir können nicht alle Bedrohungen in diesem Gebiet allein bewältigen. Wir könnten eine weitere kämpfende Hand gebrauchen, $n.' WHERE `entry`=10537 && `type`=0;
UPDATE `quest_greeting` SET `content_loc3`='Ich fühle mich nie mehr warm...' WHERE `entry`=10926 && `type`=0;
UPDATE `quest_greeting` SET `content_loc3`='Seid gegrüßt, $N. Ich bin Herzog Nicholas Zverenhoff von der Argentumdämmerung.' WHERE `entry`=11039 && `type`=0;
UPDATE `quest_greeting` SET `content_loc3`='Desolace ist kein so schlechter Ort, wenn man sich nicht an den ständigen Belästigungen durch die Zentauren stört.' WHERE `entry`=11259 && `type`=0;
UPDATE `quest_greeting` SET `content_loc3`='In meinem Geschäftsplan war nie vorgesehen, riesige Kodos zu beherbergen... vielleicht hätte ich stattdessen in die Unterwasser-Korbflechterei einsteigen sollen.' WHERE `entry`=11596 && `type`=0;
UPDATE `quest_greeting` SET `content_loc3`='Die Argentumdämmerung wird niemanden abweisen, der bereit ist, sich für unsere Sache zu opfern.' WHERE `entry`=16281 && `type`=0;
UPDATE `quest_greeting` SET `content_loc3`='Der Lichkönig hat aus dem eisigen Norden Krieg gegen uns geführt, und nur wir von der Argentumdämmerung stehen ihm im Weg.' WHERE `entry`=16361 && `type`=0;
UPDATE `quest_greeting` SET `content_loc3`='Guten Tag, Bürger. Seid Ihr gekommen, um uns gegen die Geißel zu helfen?' WHERE `entry`=16478 && `type`=0;
UPDATE `quest_greeting` SET `content_loc3`='Guten Tag, Bürger. Seid Ihr gekommen, um uns gegen die Geißel zu helfen?' WHERE `entry`=16494 && `type`=0;
