UPDATE `locales_quest` SET `RequestItemsText_loc3` = 'Ihr werdet es in diesem Leben nicht sehr weit bringen, wenn Ihr nicht einmal die schwächsten aller Zombies töten könnt, $N. Kommt nicht wieder, bevor Ihr nicht von Eurem Erfolg berichten könnt.' WHERE `entry`=364;
