UPDATE `locales_quest` SET `OfferRewardText_loc1` = '당신, 보기보다는 꽤 센데!' WHERE `entry`=1640;
UPDATE `locales_quest` SET `RequestItemsText_loc1` = '내 눈에 흙이 들어가기 전에는 이 술잔을 넘겨줄 수 없다니까...' WHERE `entry`=1640;
