UPDATE `locales_quest` SET `OfferRewardText_loc6` = 'Aquí tienes tu encaje abisal, $n. Es cómodo y duradero y alberga la magia del cabello de hermana sonriente.$B$BDisfrútalo y si lo encuentras adecuado menciónaselo a Deino, la maga. Me gustaría estar de su lado...' WHERE `entry`=1946;
