UPDATE `locales_quest` SET `RequestItemsText_loc3` = 'Ihr habt eine kränkliche, verderbte Ausgabe von etwas gefunden, das wie eine Liedblumenpflanze aussieht. Ein stechender, ungesunder Geruch geht von der Pflanze aus. Es muss sich dringend jemand um die Planzen kümmern, wenn sie wieder zu ihrem Normalzustand zurückkehren soll.' WHERE `entry`=2878;
