UPDATE `locales_quest` SET `RequestItemsText_loc3` = 'Ihr habt das zwar bereits erledigt, aber wir nehmen weiterhin alle Troll-Stammeshalsketten an, die Ihr findet und eintauschen wollt. Indem Ihr sie bei mir eintauscht, verbessert Ihr weiter Eure Stellung unter den Wildhammers. Wenn Ihr für uns kämpft, dann kämpfen wir auch für Euch!$B$BIch benötige 5 Troll-Stammeshalsketten, damit ich es gelten lassen kann, $N.' WHERE `entry`=2881;
