UPDATE `locales_quest` SET `RequestItemsText_loc8` = 'Большие праздничные ракеты ярко вспыхивают в небе – с их красотой могут посоперничать только сигнальные ракеты!$B$BПринеси мне монет наследия, Мишкин, и я научу тебя их делать.' WHERE `entry`=8879;
