UPDATE `quest_greeting` SET `content_loc2`='Le devoir des Outrunners est d\'assurer la sécurité de ceux qui voyagent à travers les plaines de Mulgore. Ceux qui menacent la sécurité de la patrie taurine risquent d\'être punis par nos soins.' WHERE `entry`=2988 && `type`=0;
